declare @defaultId varchar(100) = (
select top(1) [Role].[RoleId] from [Role]
where [Role].[Name] = 'default' );
insert into UserRole (UserId, RoleId) select [User].[Id],  @defaultId from [User];

declare @bossId varchar(100) = (select [User].Id from [User] where [User].UserName = '楊錦山')
declare @bossRole varchar(100) = (select [Role].RoleId from [Role] where [Role].[Name] = 'boss')

update UserRole set RoleId = @bossRole where UserRole.UserId = @bossId;

declare @adminId varchar(100) = (select [User].Id from [User] where [User].UserName = '劉昌衡')
declare @admin2Id varchar(100) = (select [User].Id from [User] where [User].UserName = '楊富婷')
declare @adminRole varchar(100) = (select [Role].RoleId from [Role] where [Role].[Name] = 'admin')

update UserRole set RoleId = @adminRole where UserRole.UserId = @adminId or UserRole.UserId = @admin2Id;

declare @managerId varchar(100) = (select [User].Id from [User] where [User].UserName = '徐家得')
declare @managerRole varchar(100) = (select [Role].RoleId from [Role] where [Role].[Name] = 'manager')

update UserRole set RoleId = @managerRole where UserRole.UserId = @managerId;

select u.[Id], u.UserName, u.Email,r.[Name], ur.RoleId from [User] as u
left join [UserRole] as ur on ur.[UserId] = u.Id
left join [Role] as r on r.RoleId = ur.RoleId
order by u.UserName asc;